import React from "react";
import {Redirect} from "react-router-dom";
import UserService from "../services/user.service";
import Card from "react-bootstrap/Card";
import styles from "./tables.module.css";
import Button from "react-bootstrap/Button";
import ApiService from "../services/api.service";
import {ListModel} from "../models/list.model";

export default class TablesComponent extends React.Component {

    state = {
        redirect: false,
        redirectUrl: '',
        refresh: false
    }

    lists: ListModel[] = [];

    private refresh(): void {
        this.setState({refresh: false});
        this.setState({refresh: true});
    }

    handleBackToBoards() {
        this.setState({
            redirect: true,
            redirectUrl: 'tables'
        })
    }

    async componentDidMount() {
        this.lists = await ApiService.GetLists();
        this.refresh();
    }

    private createLists() {
        let table = [];
        if (this.state.refresh) {
            let view: any[] = [];
            this.lists.forEach(tab => {
                view.push(<Card className={styles.card}>
                    <Card.Body>
                        <Card.Title>
                            {tab.getListName()}
                        </Card.Title>
                        <div className={styles.btnGroup}>
                            <Button className={styles.btn} variant={"success"}>Open</Button>
                            <Button className={styles.btn} variant={"danger"}>Delete</Button>
                        </div>
                    </Card.Body>
                </Card>)
            });
            table.push(view);
        }
        return table;
    }

    render = () => {
        UserService.subject.subscribe(() => {
            this.setState({
                redirect: true,
                redirectUrl: '/login'
            })
        });
        return <>
            {this.renderRedirect()}
            <button onClick={() => this.handleBackToBoards()}>Back to boards</button>
        </>;
    }

    private renderRedirect() {
        if (this.state.redirect) {
            return <Redirect to={'/'  + this.state.redirectUrl}/>
        }
    }
}
