import React from "react";
import {Redirect} from "react-router-dom";
import UserService from "../services/user.service";
import Card from "react-bootstrap/Card";
import styles from "./tables.module.css";
import Button from "react-bootstrap/Button";
import ApiService from "../services/api.service";
import {ListModel} from "../models/list.model";
import {CardModel} from "../models/card.model";
import {TableModel} from "../models/table.model";

export default class TablesComponent extends React.Component {

    state = {
        redirect: false,
        redirectUrl: '',
        refresh: false
    }

    lists: ListModel[] = [];
    cards: CardModel[] = [];

    private refresh(): void {
        this.setState({refresh: false});

        this.setState({refresh: true});
    }

    handleBackToBoards() {
        this.setState({
            redirect: true,
            redirectUrl: 'tables'
        })
    }

    async componentDidMount() {
        this.lists = await ApiService.GetLists(+window.location.pathname.split('/')[2]);
        const buffer = [];
        for (const list of this.lists) {
            let cards = await ApiService.GetCards(list.getListID());
            for (const card of cards) {
                buffer.push(card);
            }
        }
        this.cards = buffer;
        this.refresh();
    }

    private createLists() {
        let lists = [];
        if (this.state.refresh) {
            let view: any[] = [];
            this.lists.forEach(list => {
                view.push(<Card className={styles.card}>
                    <Card.Body>
                        <Card.Title className={styles.listTitle}>
                            {list.getListName()}
                            <Button className={styles.cardButton} onClick={() => {
                                this.deleteList(list);
                            }} variant={"danger"}>Delete</Button>
                        </Card.Title>
                        {this.createCards(list.getListID())}
                    </Card.Body>
                </Card>)
            });
            lists.push(view);
        }
        return lists;
    }

    render = () => {
        UserService.subject.subscribe(() => {
            this.setState({
                redirect: true,
                redirectUrl: '/login'
            })
        });
        return <>
            {this.renderRedirect()}
            <button onClick={() => this.handleBackToBoards()}>Back to boards</button>
            <button onClick={() => this.toggleCreationView()}>New list</button>
            <div className={styles.container}>
                <div className={styles.creationForm}>
                    {/*{this.createTableCreationView()}*/}
                </div>
                <div className={styles.tables}>
                    {this.createLists()}
                </div>
                <div className={styles.createNewButtonDiv}>
                    {/*<Button onClick={() => this.toggleCreationView(true)} className={styles.createNewButton}>*/}
                    {/*    Create table*/}
                    {/*</Button>*/}
                </div>
            </div>
        </>;
    }

    private toggleCreationView(status: boolean) {
        this.setState({creationView: status})
    }

    private renderRedirect() {
        if (this.state.redirect) {
            return <Redirect to={'/' + this.state.redirectUrl}/>
        }
    }

    private createCards(list_id: number) {
        let cards = [];
        if (this.state.refresh) {
            let view: any[] = [];
            this.cards.forEach(card => {
                if (card.getCardListID() === list_id) {
                    view.push(<Card className={styles.card}>
                        <Card.Body>
                            <Card.Title>
                                {card.getCardName()}
                            </Card.Title>
                            <div className={styles.btnGroup}>
                                <Button className={styles.cardButton} onClick={() => {
                                    this.deleteCard(card);
                                }} variant={"danger"}>Delete</Button>
                            </div>
                        </Card.Body>
                    </Card>)
                }
            });
            cards.push(view);
        }
        return cards;
    }

    private deleteCard(card: CardModel) {
        const index = this.cards.indexOf(card);
        if (index !== -1) {
            ApiService.DeleteCard(card).then(() => {
                this.cards.splice(index, 1);
                this.refresh();
            });
        }
    }

    private deleteList(list: ListModel) {
        const index = this.lists.indexOf(list);
        if (index !== -1) {
            ApiService.DeleteList(list).then(() => {
                this.lists.splice(index, 1);
                this.refresh();
            });
        }
    }
}
