import React from "react";
import {Redirect} from "react-router-dom";
import UserService from "../services/user.service";
import Card from "react-bootstrap/Card";
import styles from "./tables.module.css";
import Button from "react-bootstrap/Button";
import ApiService from "../services/api.service";
import {ListModel} from "../models/list.model";

export default class TablesComponent extends React.Component {

    state = {
        redirect: false,
        redirectUrl: '',
        refresh: false
    }

    lists: ListModel[] = [];

    private refresh(): void {
        this.setState({refresh: false});

        this.setState({refresh: true});
    }

    handleBackToBoards() {
        this.setState({
            redirect: true,
            redirectUrl: 'tables'
        })
    }

    async componentDidMount() {
        this.lists = await ApiService.GetLists(+window.location.pathname.split('/')[2]);
        console.log(this.lists);
        this.refresh();
    }

    private createLists() {
        let lists = [];
        if (this.state.refresh) {
            let view: any[] = [];
            this.lists.forEach(tab => {
                view.push(<Card className={styles.card}>
                    <Card.Body>
                        <Card.Title>
                            {tab.getListName()}
                        </Card.Title>
                        {this.createCards()}
                    </Card.Body>
                </Card>)
            });
            lists.push(view);
        }
        return lists;
    }

    render = () => {
        UserService.subject.subscribe(() => {
            this.setState({
                redirect: true,
                redirectUrl: '/login'
            })
        });
        return <>
            {this.renderRedirect()}
            <button onClick={() => this.handleBackToBoards()}>Back to boards</button>
            {this.createLists()}
        </>;
    }

    private renderRedirect() {
        if (this.state.redirect) {
            return <Redirect to={'/'  + this.state.redirectUrl}/>
        }
    }

    private createCards() {
        let cards = [];
        if (this.state.refresh) {
            let view: any[] = [];
            this.lists.forEach(card => {
                view.push(<Card className={styles.card}>
                    <Card.Body>
                        <Card.Title>
                            {card.getCardName()}
                        </Card.Title>
                        {this.createCards()}
                    </Card.Body>
                </Card>)
            });
            cards.push(view);
        }
        return cards;
    }
}
